import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  titulo:string = "Welcome";
  mensaje:string = "Saludos, soy un mensaje de prueba."
}
    <div class="container">
        <div class="row">
            <div class="col-md-6">
                <h1>{{titulo}}</h1>
                <p>{{mensaje}}</p>  
            </div>
            <div class="col-md-2">

            </div>
            <div class="col-md-4">
                <app-hijo [nombtitulo]="titulo" (cambiarTitulo)="titulo = $event" [nombmensaje]="mensaje" (cambiarMensaje)="mensaje = $event"></app-hijo>
            </div>
        </div>
    </div>
